import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./ExpensesFilter";
import { useState } from "react";
import Card from "../UI/Card";

function Expenses(props) {
  const [enteredYear, setYear] = useState("2020");

  const yearChangeHandler = (selectedYear) => {
    setYear(selectedYear);
  };

  const filterByYear = (year) =>{
    return year === enteredYear;
  };

  /*for (let index = 0; index < props.expenseItems.length; index++) {
    const element = props.expenseItems[index];
    expenseItemsJsx.push(
      <ExpenseItem
        title={element.title}
        amount={element.amount}
        date={element.date}
      />
    );
  }*/


  return (
    <div>
      <ExpenseFilter
        selected={enteredYear}
        onSelectedYear={yearChangeHandler}
      />
      <Card className="expenses">
        {props.expenseItems.filter().map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
