import React from 'react';
import { useState } from 'react';
import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [enteredYear, setYear] = useState("");

  const selectHandler=(event)=>{
    event.PreventDefault();
    setYear(event.target.value);
    const selectedYear = enteredYear;
    props.onSelectedYear(selectedYear);
  };

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={selectHandler}>
          <option value='2022' >2022</option>
          <option value='2021' onChange={yearChangeHandler}>2021</option>
          <option value='2020' onChange={yearChangeHandler}>2020</option>
          <option value='2019' onChange={yearChangeHandler}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
